function count_down(o) {
    var conn, s, datatime = /^[\d]{4}-[\d]{1,2}-[\d]{1,2}( [\d]{1,2}:[\d]{1,2}(:[\d]{1,2})?)?$/gi, str = "";
    if (!o.match(datatime)) return alert("参数格式为2020-01-01[ 01:01[:01]].\r其中[]内的内容可省略"), !1;
    var sec = (new Date(o.replace(/-/gi, "/")).getTime() - (new Date).getTime()) / 1e3;
    sec > 0 ? conn = "还有" : (conn = "已过去", sec *= -1), s = {
        "天": sec / 24 / 3600,
        "小时": sec / 3600 % 24,
        "分": sec / 60 % 60,
        "秒": sec % 60
    };
    for (i in s) Math.floor(s[i]) > 0 && (str += Math.floor(s[i]) + i);
    0 == Math.floor(sec) && (str = "0秒"), document.getElementById("show").innerHTML = str, setTimeout(function () {
        count_down(o)
    }, 1e3)
}